import { Component, OnInit } from '@angular/core';
import { AuthorizeService } from '../authorize.service';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

@Component({
  selector: 'app-login-menu',
  templateUrl: './login-menu.component.html',
  styleUrls: ['./login-menu.component.css']
})
export class LoginMenuComponent implements OnInit {
  public isAuthenticated?: Observable<boolean>;
  public userName?: Observable<string | null | undefined>;

  constructor(private authorizeService: AuthorizeService) { }

  ngOnInit() {
    this.isAuthenticated = this.authorizeService.isAuthenticated();
    this.userName = this.authorizeService.getUser().pipe(map(u => u && u.name));
  }
}
<ul class="navbar-nav" *ngIf="isAuthenticated | async">
  <li class="nav-item" [routerLinkActive]="['link-active']">
    <a class="nav-link text-light" [routerLink]="['/test-sound']">サウンドテスト</a>
  </li>
  <li class="nav-item">
    <a class="nav-link text-light" [routerLink]='["/authentication/profile"]' title="Manage">{{ userName | async }}</a>
  </li>
  <li class="nav-item">
    <a class="nav-link text-light" [routerLink]='["/authentication/logout"]' [state]='{ local: true }' title="Logout">ログアウト</a>
  </li>
</ul>
<ul class="navbar-nav" *ngIf="(isAuthenticated | async) !== true">
  <li class="nav-item">
        <a class="nav-link text-light" [routerLink]='["/authentication/register"]'>登録</a>
    </li>
    <li class="nav-item">
        <a class="nav-link text-light" [routerLink]='["/authentication/login"]'>ログイン</a>
    </li>
</ul>
