import { Component, OnInit, Input } from '@angular/core';
import { AuthorizeService } from '../authorize.service';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { UntypedFormGroup, UntypedFormBuilder, UntypedFormControl, Validators, } from '@angular/forms';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { ModalService } from './modal.service';
import { NgbActiveModal, NgbModal } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-modal',
  templateUrl: './modal.component.html'
})
export class ModalComponent implements OnInit {
  public isAuthenticated?: Observable<boolean>;

  public title = "ボタン設定";
  public description = "ボタンの動作設定を行います。";

  public panelNum = 1;
  public buttonRow = 0;
  public buttonCol = 0;
  public buttonName = "";
  public buttonType = [
    { name: 'SOUND', type: 1, displayName: '効果音再生' }
    , { name: 'COMMAND', type: 2, displayName: 'プログラム実行' }
  ];
  public buttonCommand = "";
  public buttonIcon = "";
  public buttonDescription = "";

  buttonConfig: UntypedFormGroup;

  constructor(private formBuilder: UntypedFormBuilder, private authorizeService: AuthorizeService, public service: ModalService, private modalService: NgbModal, public activeModal: NgbActiveModal) {
    this.buttonConfig = formBuilder.group(
      {
        selectButtonType: ['', { updateOn: 'change' }]
      }
    );
  }

  get selectTest(): UntypedFormControl {
    return this.buttonConfig.get('selectButtonType') as UntypedFormControl;
  }

  ngOnInit(): void {
    this.isAuthenticated = this.authorizeService.isAuthenticated();
    this.selectTest.setValue(this.buttonType[1]);
  }
}
<div class="modal fade" id="editModal" tabindex="-1" aria-labelledby="editModalLabel" aria-hidden="true" *ngIf="isAuthenticated | async">
  <div class="modal-dialog modal-dialog-centered">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="editModalLabel">{{ title }} </h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close" (click)="activeModal.dismiss('Cross click')"></button>
      </div>

      <div class="modal-body">
        <div class="row">{{description}}</div>
        <div class="row">
          <form [formGroup]="buttonConfig">
            <div class="form-group">
              <div class="row">
                <label for="panelNum">パネルナンバー：</label>
                <input class="form-control" type="number" formControlName="panelNum" [value]="panelNum" required />
              </div>
              <div class="row">
                <label for="buttonRow">ボタン行番：</label>
                <input class="form-control" type="number" formControlName="buttonRow" [value]="buttonRow" required />
              </div>
              <div class="row">
                <label for="buttonCol">ボタン列番：</label>
                <input class="form-control" type="number" formControlName="buttonCol" [value]="buttonCol" required />
              </div>
              <div class="row">
                <label for="buttonName">ボタン名：</label>
                <input class="form-control" type="text" formControlName="buttonName" [value]="buttonName" required />
              </div>
              <div class="row">
                <label for="selectButtonType">ボタン動作種別：</label>
                <select id="ddl-btn-type" formControlName="selectButtonType">
                  <option>選択してください</option>
                  <option *ngFor="let buttonType of buttonType" [ngValue]="buttonType">{{ buttonType.displayName }}</option>
                </select>
              </div>
              <div class="row">
                <label for="buttonCommand">実行ファイル：</label>
                <input class="form-control" type="file" formControlName="buttonCommand" [value]="buttonCommand" required />
              </div>
              <div class="row">
                <label for="buttonIcon">ボタンアイコン：</label>
                <input class="form-control" type="file" formControlName="buttonIcon" [value]="buttonIcon" />
              </div>
              <div class="row">
                <label for="buttonDescription">ボタン説明：</label>
                <textarea class="form-control" rows="5" formControlName="buttonDescription" [value]="buttonDescription"></textarea>
              </div>
            </div>
          </form>
        </div>
      </div>

      <div class="modal-footer d-flex justify-content-between align-items-center">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">閉じる</button>
        <button type="button" class="btn btn-warning" data-bs-toggle="modal" data-bs-target="#validationModal">保存</button>
      </div>
    </div>
  </div>
</div>
